import{_ as a,c as r,o as t,ah as s}from"./chunks/framework.D1nhdr5e.js";const g=JSON.parse(`{"title":"Liste d'articles autour des opérations de sauvegarde et de restauration de la base de données PostgreSQL","description":"","frontmatter":{"title":"Liste d'articles autour des opérations de sauvegarde et de restauration de la base de données PostgreSQL","head":[["meta",{"property":"og:title","content":"Liste d'articles autour des opérations de sauvegarde et de restauration de la base de données PostgreSQL"}],["meta",{"property":"og:description","content":"Cette page regroupe des séries d'article sur PostgreSQL en français concernant la sauvegarde et la restauration de la base de données"}],["meta",{"property":"og:type","content":"article"}],["link",{"rel":"canonical","href":"https://5432.fr/articles/sauvegarde"}],["meta",{"property":"og:url","content":"https://5432.fr/articles/sauvegarde"}],["meta",{"property":"og:image","content":"https://5432.fr/postgresql-200x200.png"}]]},"headers":[],"relativePath":"articles/sauvegarde.md","filePath":"articles/sauvegarde.md","lastUpdated":1757145130000}`),o={name:"articles/sauvegarde.md"};function n(i,e,d,l,u,p){return t(),r("div",null,[...e[0]||(e[0]=[s('<h1 id="sauvegarde-et-restauration" tabindex="-1">Sauvegarde et restauration <a class="header-anchor" href="#sauvegarde-et-restauration" aria-label="Permalink to “Sauvegarde et restauration”">​</a></h1><h2 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-label="Permalink to “Introduction”">​</a></h2><p>La sauvegarde et la restauration font référence aux technologies et pratiques permettant de réaliser des copies périodiques de données et d&#39;applications sur un périphérique secondaire distinct, puis d&#39;utiliser ces copies pour récupérer les données et les applications, ainsi que les opérations commerciales dont elles dépendent.</p><p>La sauvegarde et la restauration sont utilisées si les données et les applications d&#39;origine sont perdues ou endommagées en raison d&#39;une panne de courant, d&#39;une cyberattaque, d&#39;une erreur humaine, d&#39;une catastrophe ou de tout autre événement imprévu.</p><h2 id="sous-postgresql" tabindex="-1">Sous PostgreSQL <a class="header-anchor" href="#sous-postgresql" aria-label="Permalink to “Sous PostgreSQL”">​</a></h2><p>Sous PostgreSQL par défaut il existe 3 type de sauvegarde.</p><ul><li>Sauvegarde de type SQL (Dump)</li><li>Sauvegarde au niveau du système de fichier</li><li>Sauvegarde en archivage continue</li></ul><h3 id="sauvegarde-sql" tabindex="-1">Sauvegarde SQL <a class="header-anchor" href="#sauvegarde-sql" aria-label="Permalink to “Sauvegarde SQL”">​</a></h3><p>La sauvegarde SQL (également appelé Dump) se réalise avec le programme founit en standard <a href="https://docs.postgresql.fr/current/app-pgdump.html" target="_blank" rel="noreferrer">pg_dump</a>.</p><p>la restauration de cette sauvegarde se fera avec le programme <a href="https://docs.postgresql.fr/current/app-pgrestore.html" target="_blank" rel="noreferrer">pg_restore</a></p><h2 id="articles" tabindex="-1">Articles <a class="header-anchor" href="#articles" aria-label="Permalink to “Articles”">​</a></h2><h3 id="sauvegarde" tabindex="-1">Sauvegarde <a class="header-anchor" href="#sauvegarde" aria-label="Permalink to “Sauvegarde”">​</a></h3><ul><li><a href="http://laetitia-avrot.blogspot.fr/2017/02/faire-une-sauvegarde-avec-pgbackrest.html" target="_blank" rel="noreferrer">Faire une sauvegarde avec pgBackRest</a></li></ul><h3 id="restauration" tabindex="-1">Restauration <a class="header-anchor" href="#restauration" aria-label="Permalink to “Restauration”">​</a></h3><ul><li><a href="http://laetitia-avrot.blogspot.fr/2017/02/restaurer-avec-pgbackrest.html" target="_blank" rel="noreferrer">Restaurer avec pgBackRest</a></li></ul>',15)])])}const h=a(o,[["render",n]]);export{g as __pageData,h as default};
