import{_ as r,c as a,o as s,ah as t}from"./chunks/framework.D1nhdr5e.js";const u=JSON.parse(`{"title":"Hebergeurs PostgreSQL","description":"","frontmatter":{"head":[["meta",{"property":"og:title","content":"Hebergeur PostgreSQL"}],["meta",{"property":"og:description","content":"Liste d'hébergeur proposant la solution PostgreSQL"}],["link",{"rel":"canonical","href":"https://5432.fr/services/hebergeurs"}]]},"headers":[],"relativePath":"services/hebergeurs.md","filePath":"services/hebergeurs.md","lastUpdated":1757145130000}`),o={name:"services/hebergeurs.md"};function n(i,e,l,p,c,d){return s(),a("div",null,[...e[0]||(e[0]=[t('<h1 id="hosting" tabindex="-1">Hebergeurs PostgreSQL <a class="header-anchor" href="#hosting" aria-label="Permalink to “Hebergeurs PostgreSQL”">​</a></h1><p>Liste des sociétés offrant des hébergements pour PostgreSQL, pour des sites web ou des applicatifs metiers</p><h2 id="ovh" tabindex="-1">OVH <a class="header-anchor" href="#ovh" aria-label="Permalink to “OVH”">​</a></h2><p><a href="https://www.ovhcloud.com/fr/public-cloud/postgresql/" target="_blank" rel="noreferrer">OVH</a> propose PostgreSQL à son catalogue.</p><p>Accélérez votre activité en déployant un cluster managé PostgreSQL en quelques clics. Bénéficiez d&#39;un moteur de bases de données relationnelles open-source capable de gérer de lourdes charges de travail. Concentrez-vous ainsi sur le développement de vos fonctionnalités applicatives, nous nous chargeons de la gestion du service : configuration, maintenance, backup, haute disponibilité et mises à jour.</p><h2 id="ionos" tabindex="-1">IONOS <a class="header-anchor" href="#ionos" aria-label="Permalink to “IONOS”">​</a></h2><p><a href="https://cloud.ionos.fr/managed/dbaas/postgresql" target="_blank" rel="noreferrer">IONOS (ex 1&amp;1)</a> vous propose des bases de données puissantes et évolutives</p><p>Créez facilement vos propres bases de données PostgreSQL personnalisées et confiez-nous la gestion des bases de données pour vos applications et charges de travail complexes.</p><h2 id="clever-cloud" tabindex="-1">Clever Cloud <a class="header-anchor" href="#clever-cloud" aria-label="Permalink to “Clever Cloud”">​</a></h2><p><a href="https://www.clever-cloud.com/postgresql-hosting" target="_blank" rel="noreferrer">Clever Cloud</a> vous propose un service PostgreSQL entièrement managé avec des outils de supervision, de sauvegarde et de migration.</p><h2 id="scalingo" tabindex="-1">Scalingo <a class="header-anchor" href="#scalingo" aria-label="Permalink to “Scalingo”">​</a></h2><p><a href="https://scalingo.com/fr/databases/postgresql" target="_blank" rel="noreferrer">Scalingo</a> vous propose un hébergement PostgreSQL managée</p><p>Provisionnez une base PostgreSQL en moins de temps qu&#39;il n&#39;en faut pour chercher un café. Complètement administré, hautement disponible &amp; prêt pour la production.</p><h2 id="kinsta" tabindex="-1">Kinsta <a class="header-anchor" href="#kinsta" aria-label="Permalink to “Kinsta”">​</a></h2><p><a href="https://kinsta.com/fr/hebergement-bases-de-donnees/postgresql/" target="_blank" rel="noreferrer">Kinsta</a> vosu propose un hébergement PostgreSQL facile et puissant</p><p>Rejoignez plus de 55.000 développeurs et entrepreneurs sur le PaaS de Kinsta. Notre hébergement PostgreSQL vous permet de vous concentrer sur d&#39;autres tâches.</p><h2 id="gandi" tabindex="-1">Gandi <a class="header-anchor" href="#gandi" aria-label="Permalink to “Gandi”">​</a></h2><p><a href="https://docs.gandi.net/fr/hebergement_web/gestion_base_donnees/postgresql.html" target="_blank" rel="noreferrer">Gandi</a></p><h2 id="ikoula" tabindex="-1">Ikoula <a class="header-anchor" href="#ikoula" aria-label="Permalink to “Ikoula”">​</a></h2><p><a href="https://www.ikoula.com/fr/cloud-public/oneclick/postgresql" target="_blank" rel="noreferrer">Ikoula</a></p><h2 id="bearstech" tabindex="-1">Bearstech <a class="header-anchor" href="#bearstech" aria-label="Permalink to “Bearstech”">​</a></h2><p><a href="https://bearstech.com/technologies-expertises/postgresql/" target="_blank" rel="noreferrer">Bearstech</a> vous propose son expertise autour de PostgreSQL</p>',22)])])}const g=r(o,[["render",n]]);export{u as __pageData,g as default};
